.card {
   max-width: rem(322px);
   margin: 0 auto;
   height: rem(456px);
   box-shadow: 0px 2px 8px rgba(0, 0, 0, 0.25);
   padding: rem(24px) 0 rem(32px) 0;
   text-align: center;
   background-color: var(--color-white);
   color: var(--color-standart);
   border-radius: rem(8px);
   transition: all .3s;
   &.card_margin {
      margin-bottom: rem(32px);
   }
   &__services {
      font-weight: 700;
      font-size: rem(24px);
   }
   &__subtitle {
      margin-bottom: rem(16px);
   }
   &__month {
      display: flex;
      justify-content: center;
      align-items: center;
   }
   &__cost , &_d  {
      font-weight: 700;
      color: var(--color-green);
   }
   &__cost {
      font-size: rem(78px);
   }
   &__wrapper {
      display: flex;
      flex-direction: column;
      align-items: flex-start;
      padding-top: rem(25px);
      margin-left: rem(8px);
      margin-bottom: rem(16px);
   }
   &__unit {
      font-size: rem(30px);
      margin-bottom: rem(4px);
      line-height: 1;
      font-weight: 600;
      color: var(--color-green);
   }
   &__time {
      color: var(--color-month);
   }
   &__additionally {
      margin-bottom: rem(38px);
      span {
         display: block;
         font-weight: 500;
      }
      span:not(:last-child) {
         margin-bottom: rem(16px);
      }
   }
   .button {
      transition: all .2s;
      font-weight: 500;
   }
   &:hover {
      background-color: var(--color-green);
      .card__time ,.card__cost , .card__unit , .card__subtitle , .card__services , .card__additionally {
         color: var( --color-white);
      }
      .button{
         background-color: var( --color-white);
         color: var(--color-black);
      }
   }
   .button:hover {
      border: 1px solid #30444E;
      box-shadow: 0px 2px 0 #30444E, 2px 4px 6px #30444E;
      -webkit-transition: all 150ms linear;
      transition: all 150ms linear;
   }
}

@media screen and (min-width:992) {
   .card {
      &.card_margin {
         margin-bottom: rem(0px);
