commits.content:
.popup__bg-1 , .popup__bg-2{
   position: fixed;
   top: 0;
   left: 0;
   width: 100%;
   height: 100vh;
   background: rgba(0,0,0,0.5);
   opacity: 0;
   pointer-events: none;
   // transition: 0.5s all;
   z-index: 100;
   overflow-y: auto;
   overflow-x: hidden;
}

.popup__bg-1.active , .popup__bg-2.active{
   opacity: 1;
   pointer-events: all;
   transition: 0.5s all;
}

.popup-1 , .popup-2 {
   position: absolute;
   top: 50%;
   left: 50%;
   transform: translate(-50%, -50%) scale(0);
   width: 80%;
   height: auto;
   overflow-y: auto;
   padding: rem(35px);
   background: #fff;
   background-color: var(--color-bg-white);
   transition: 0.5s all;
   .table {
         width: 100%;
         margin-bottom: rem(25px);
         display: flex;
         flex-direction: column-reverse;
         &__text {
            color: #000;
         }
      }
      .input {
         height: rem(45px);
         font-size: rem(18px);
         border: none;
         outline: none;
         border-bottom: rem(1px) solid #cfd0d3;
         padding: 0 rem(5px);
         width: 100%;

      &:focus {
         border-bottom: rem(1px) solid var(--color-green);
      }
      &:focus + .label__text {
         color: var(--color-green);
      }
   }
   .checkbox {
      margin-top: rem(10px);
      margin-bottom: rem(20px);
      color:  var(--color-text-black);
   }
   button {
      width: 100%;
      height: rem(45px);
      color: #fff;
      font-size: rem(18px);
      border: rem(2px) solid var(--color-green);
      background: var(--color-green);
      cursor: pointer;
      text-transform: uppercase;
      transition: 0.5s all;
      &:hover {
         background: #fff;
         color: var(--color-green);
         transition: 0.5s all;
      }      
   }
}

.popup-1 {
   input[type="checkbox"] {
      display: none;
   }
}

.popup-2 {
   .checkbox {
      &__input {
         display: none;
      }
      &__tabel {
         padding-left: rem(10px);
         &::before {
            content: '';
            display: inline-block;
            width: rem(20px);
            height: rem(20px);
            background-color: rgb(255, 255, 255);
            border: solid 1px #000;
            vertical-align: middle;
            margin-left: rem(-10px);
            margin-right: rem(10px);
         }
      }
      input[type='checkbox']:checked + label::before {
         background-image: url('https://cdn-icons-png.flaticon.com/512/60/60731.png');
         background-size: rem(16px);
         background-position: center;
         background-repeat: no-repeat;
      }
   }
}

.popup-1.active , .popup-2.active {
   transform: translate(-50%, -50%) scale(1);
   transition: 0.5s all;
}

.close-popup-1 , .close-popup-2 {
   position: absolute;
   top: rem(10px);
   right: rem(10px);
   cursor: pointer;
}

.popup-1 .label , .popup-2 .label{
   width: 100%;
   margin-bottom: rem(25px);
   display: flex;
   flex-direction: column-reverse;
}

.popup-1 .label__text  , .popup-2 .label__text  {
   font-size: rem(14px);
   text-transform: uppercase;
   font-weight: 500;
   color: #cfd0d3;
   margin-bottom: rem(5px);
}

@media screen and (min-width:768px) {
   .popup-1 , .popup-2 {
      width: 70%;
      height: auto;
   }
}

@media screen and (min-width:992px) {
   .popup-1 , .popup-2 {
      width: rem(600px);
      padding: rem(45px);
      width: auto;
   }
}
