commits.content:
.section {
   padding-bottom: rem(100px);
   &_bg {
      @extend .bg-light-dark;
   }
   &__heading {
      text-align: center;
      margin-bottom: rem(50px);
      .subtitle {
         margin-top: rem(8px);
      }
      .title__xs {
         margin-bottom: rem(32px);
      }
   }
   &_content{
      margin-top: 0;
      .title {
         text-align: center;
      }
   }
   &_info {
      .title_xs {
         margin-bottom: rem(8px);
      }
      .title_sm {
         margin-bottom: rem(24px);
      }
   }
   &_for-you {
      &.section_bg {
         padding-top: rem(24px);
         padding-bottom: rem(38px);
      }
      .for-you {
         @extend .m-b;
      }
   }
   &_stories {
      .strategies {
         @extend .m-b;
      }
   }
   //section for-you Ð¸ process 
   &_bg-t , &_bg-b {
      width: 100%;
      overflow: hidden;
      position: relative;
      &::after {
         content: "";
         position: absolute;
         top: 0;
         left: 50%;
         transform: translateX(-50%);
         width: 270%;
         height: 100%;
         z-index: -1;
      }
   }
   &_bg-b {
      padding-bottom: rem(40px);
      &::after {
         border-radius:  0 0 50% 50%;
      }
   }
   &_bg-t {
      padding-top: rem(40px);
      &::after {
         border-radius: 50% 50% 0 0;
      }
   }
   &_company {
      margin-bottom: rem(60px);
      .company {
         margin-top: rem(80px);
      }
      .company__img {
         margin-bottom: rem(60px);
      }
   }
   &_ticket {
      text-align: center;
      padding-top: rem(35px);
      padding-bottom: rem(45px);
      .ticket__wrapper{
         margin-bottom: rem(65px);
      }
   }
   &_map {
      margin-bottom: rem(50px);
   }
}

[data-theme="light"] {
   .section_bg-b {
      &::after {
         background:  linear-gradient(179.59deg,rgba(255, 255, 255, 0.0001) 0.35%, #EDEDED  99.63%);
      }
   }
   .section_bg-t {
      &::after {
         background: linear-gradient(179.59deg, #EDEDED 0.35%, rgba(255, 255, 255, 0.0001) 99.63%);
      }
   }
}

[data-theme="dark"]  {
   .section_bg-b{
      &::after {
         background: linear-gradient(179.59deg,rgba(67, 67, 67, 0.0001)  0.35%, #22343D  99.63%);
      }
   }
   .section_bg-t {
      &::after {
         background: linear-gradient(179.59deg, #22343D 0.35%, rgba(67, 67, 67, 0.0001) 99.63%);
      }
   }
}

@media screen and (min-width:575px) {
   .section {
      &_bg-t , &_bg-b {
         &::after {
            width: 170%;
         }
      }
   }
}
@media screen and (min-width:576px) {
   .section {
      &_ticket {
         .ticket__wrapper{
            margin-bottom: 0px;
         }
      }
   }
}

@media screen and (min-width:768px)  {
   .section {
      &_strategies {
         .section__heading {
            text-align: left;
         }
      }
      &_content {
         .title {
            text-align: left;
         }
      }
   }
}

@media screen and (min-width:992px) {
   .section {
      margin-bottom: rem(150px);
      &_content{
         margin-top: 0;
         margin-bottom: rem(115px);
      }
      &_bg-t , &_bg-b {
         &::after {
            width: 150%;
         }
      }
      &_map {
         margin-bottom: rem(50px);
      }
   }
}
